local stats = {}addEventHandler( "onElementDataChange", root,	function( key, oldValue )		-- merely for the stats		--[[		local elementType = getElementType(source)		if not stats[elementType] then stats[elementType] = {} end		stats[elementType][key] = (stats[elementType][key] or 0) + 1		]]				-- this is the anticheat. it's actually effective.		if client then			outputDebugString( "client " .. getPlayerName( client ) .. "(" .. getPlayerSerial( client ) .. ") tried to set " .. tostring( source ) .. "[" .. tostring( key ) .. "] from " .. tostring( oldValue ) .. " to " .. tostring(getElementData(source, key)), 2)			setElementData( source, key, oldValue )		end	end)--[[addCommandHandler("elementdata",	function(player, command)		for k, v in pairs(stats) do			outputChatBox(k, player)			for kx, vx in pairs(v) do				outputChatBox("  " .. tostring(kx) .. "=" .. tostring(vx), player)			end		end	end)]]